import React from 'react';
import { useSelector } from 'react-redux';

import AppLayout from '../components/AppLayout';

const Home = () => {
  const { isLoggedIn } = useSelector((state) => state.user);
  const { mainPosts } = useSelector((state) => state.post);
  return (
    <AppLayout>
      {isLoggedIn && <PostForm />}
      {mainPosts.map((post) => (
        <PostCard post={post} />
      ))}
    </AppLayout>
  );
};

export default Home;
