import { ButtonGroup, Card, Popover } from 'antd';
import React from 'react';
import { RetweetOutlined, Heart } from '@ant-design';

const PostCard = ({ post }) => {
  return (
    <div>
      <Card
        cover={post.Images[0] && <PostImages images={post.Images} />}
        actions={[
          <RetweetOutlined key='retweet' />,
          <HeartOutlined key='heart' />,
          <MessageOutlined key='message' />,
          <Popover key='more' content={<ButtonGroup></ButtonGroup>}>
            <EllipsisOutlined key='retweet' />
          </Popover>,
        ]} //배열안에 jsx를 넣어줄때는 key를 항상 넣어줘야됨
      >
        <Image />
        <Content />
        <Buttons></Buttons>
      </Card>
      <CommentForm />
      <Comments />
    </div>
  );
};

export default PostCard;
