export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: '제로초',
      },
      content: '첫 번째 게시글 #해시태그',
      Images: [],
      Comments: [
        {
          User: {
            nickname: 'nero',
          },
          content: '우와',
        },
        {
          User: {
            nickname: 'hero',
          },
          content: '사고싶어요~',
        },
      ],
    },
  ],
  imagePaths: [],
  postAdded: false,
}; //더미 데이터

const ADD_POST = 'ADD_POST';
export const addPost = {
  type: ADD_POST,
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST:
    default:
      return state;
  }
};

export default reducer;
