export const initialState = {
  mainPosts: [
    {
      id: 1,

      User: {
        id: 1,
        nickname: '제로초',
      },

      content: '첫 번째 게시글 #해시태그',

      Images: [],

      Comments: [
        {
          User: {
            nickname: 'nero',
          },
          content: '우와',
        },

        {
          User: {
            nickname: 'hero',
          },
          content: '사고싶어요~',
        },
      ],
    },
  ],
  imagePaths: [],
  postAdded: false,
}; //더미 데이터

const ADD_POST = 'ADD_POST'; //오타방지용으로 변수로 빼주는게 좋다
export const addPost = {
  type: ADD_POST,
};

const dummyPost = {
  id: 2,
  content: '더미',
  User: {
    id: 2,
    nickname: '제로',
  },

  Images: [
    {
      src: '',
    },
  ],

  Comments: [
    {
      User: {
        nickname: 'nero',
      },
      content: '우와',
    },

    {
      User: {
        nickname: 'hero',
      },
      content: '사고싶어요~',
    },
  ],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST:
      return {
        ...state,
        mainPosts: [dummyPost, ...state.mainPosts], //dummyPost를 앞에다 두어야 게시물이 위에 온다.
        postAdded: true,
      };
    default:
      return state;
  }
};

export default reducer;
