import { ButtonGroup, Card, Popover } from 'antd';
import React from 'react';
import {
  RetweetOutlined,
  HeartOutlined,
  MessageOutlined,
  EllipsisOutlined,
} from '@ant-design';

const PostCard = ({ post }) => {
  const { user } = useSelector((state) => state.user);
  return (
    <div>
      <Card
        cover={post.Images[0] && <PostImages images={post.Images} />}
        actions={[
          <RetweetOutlined key='retweet' />,
          <HeartOutlined key='heart' />,
          <MessageOutlined key='message' />,
          <Popover
            key='more'
            content={
              <ButtonGroup>
                {c} && <Button>수정</Button>
                <Button type='danger'>삭제</Button>
                <Button>신고</Button>
              </ButtonGroup>
            }
          >
            <EllipsisOutlined key='retweet' />
          </Popover>,
        ]} //배열안에 jsx를 넣어줄때는 key를 항상 넣어줘야됨
      >
        <Image />
        <Content />
        <Buttons></Buttons>
      </Card>
      <CommentForm />
      <Comments />
    </div>
  );
};

PostCard.propTypes = {
  post: PropTypes.shape({
    id: PropTypes.number,
    User: PropTypes.object,
  }).inRequired,
};

export default PostCard;
